ControlPropertyState.AutoRuleBindingString:
If(
    s = 0,
    {R:l*255,G: l*255,B: l*255,A: 1}, // achromatic
    With(
        {
            q: If(l < 0.5, l * (1 + s), l + s - l * s)
        },
        With(
            {
            p: 2 * l - q
            },
            {
                R:Round(cptColourPicker.HUEtoRGB(p, q, h + 1/3) * 255,0),
                G:Round(cptColourPicker.HUEtoRGB(p, q, h) * 255,0),
                B:Round(cptColourPicker.HUEtoRGB(p, q, h - 1/3) * 255,0),
                A:1
            }
        )
    )
)

First(
    SortByColumns(
        AddColumns(
            NumberTable,
            Difference,
            Abs(ThisRecord.SampleNumberField - TargetNumber)
        ),
        "Difference"
    )
).SampleNumberField
