parameters.UserFlowContinuationSubscriptionRequest/:
{
    "type": "AdaptiveCard",
    "body": [
        {
            "type": "Container",
            "style": "emphasis",
            "items": [
                {
                    "type": "ColumnSet",
                    "columns": [
                        {
                            "type": "Column",
                            "width": "auto",
                            "items": [
                                {
                                    "type": "Image",
                                    "url": "data:image/png;base64,",
                                    "size": "Small"
                                }
                            ]
                        },
                        {
                            "type": "Column",
                            "width": "auto",
                            "items": [
                                {
                                    "type": "TextBlock",
                                    "size": "ExtraLarge",
                                    "weight": "Bolder",
                                    "text": "Validate broken links",
                                    "wrap": true
                                }
                            ]
                        },
                        {
                            "type": "Column",
                            "width": "stretch",
                            "items": [
                                {
                                    "type": "Image",
                                    "url": "https://adaptivecards.io/content/pending.png",
                                    "altText": "Pending",
                                    "height": "30px",
                                    "horizontalAlignment": "Right"
                                }
                            ]
                        }
                    ]
                }
            ],
            "bleed": true
        },
        {
            "type": "Container",
            "items": [
                {
                    "type": "ColumnSet",
                    "columns": [
                        {
                            "type": "Column",
                            "items": [
                                {
                                    "type": "TextBlock",
                                    "size": "Large",
                                    "text": "Page: @{outputs('Get_page_details')?['body/Title']}",
                                    "wrap": true
                                }
                            ],
                            "width": "stretch"
                        }
                    ]
                },
                {
                    "type": "TextBlock",
                    "text": "Potential broken links have been found on your page. Please review the links, update the page if necessary and click 'Done' once complete.",
                    "wrap": true,
                    "separator": true
                },
                {
                    "type": "TextBlock",
                    "text": "Links:",
                    "wrap": true,
                    "size": "Medium"
                },
                {
                    "type": "FactSet",
                    "spacing": "Large",
                    "facts": @{variables('Links')}
                }
            ]
        },
        {
            "type": "Container",
          "items": [
                {
                    "type": "ActionSet",
                    "actions": [
                     {
                            "type": "Action.OpenUrl",
                            "title": "Edit page",
"url":"@{outputs('Get_page_details')?['body/{Link}']}?Mode=Edit"
                        }
                    ]

                },
                {
                    "type": "ActionSet",
                    "actions": [

                        {
                            "type": "Action.Submit",
                            "title": "Done",
                            "style": "positive",
                            "data": {
                                "id": "approve",
                                "action": "approve"
                            }
                        }
                    ]
                }
            ]
        }
    ],
    "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
    "version": "1.2",
    "fallbackText": "This card requires Adaptive Cards v1.2 support to be rendered properly."
}
